import React, { useState } from "react";
import { TextField, Button, Box } from "@mui/material";

const UserForm = ({ refresh }) => {
  const [name, setName] = useState("");
  const [skillsProgress, setSkillsProgress] = useState([
    { skill: "JavaScript", progress: 0 },
    { skill: "React", progress: 0 },
    { skill: "Node.js", progress: 0 },
    { skill: "CSS", progress: 0 },
  ]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const newUser = { name, skillsProgress };

    await fetch("http://localhost:5000/api/users", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(newUser),
    });
    refresh(); // Refresh the users list
  };

  return (
    <Box>
      <TextField
        label="Student Name"
        fullWidth
        value={name}
        onChange={(e) => setName(e.target.value)}
      />
      {/* Additional fields can go here for selecting courses or progress */}
      <Button variant="contained" onClick={handleSubmit}>
        Add User
      </Button>
    </Box>
  );
};

export default UserForm;
