import React, { useState } from "react";
import {
  TextField,
  Button,
  MenuItem,
  Box,
  Typography,
  FormControl,
  InputLabel,
  Select,
  Checkbox,
  ListItemText,
  Input,
} from "@mui/material";

const courses = [
  "JavaScript",
  "React",
  "Node.js",
  "CSS",
  "Python",
  "HTML",
  "C#",
  ".NET",
  "TypeScript",
  "Ruby",
  "Go",
  "Java",
  "C++",
];

const UserForm = ({ refresh }) => {
  const [user, setUser] = useState({ username: "", courses: [] });

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name === "courses") {
      const newCourses = value;
      setUser((prev) => ({ ...prev, courses: newCourses }));
    } else {
      setUser((prev) => ({ ...prev, [name]: value }));
    }
  };

  const handleSubmit = async () => {
    await fetch(`${process.env.REACT_APP_API_BASE_URL}/api/users`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(user),
    });
    setUser({ username: "", courses: [] });
    refresh();
  };

  return (
    <Box>
      <Typography variant="h6">Add User</Typography>
      <TextField
        // fullWidth
        label="Username"
        name="username"
        value={user.username}
        onChange={handleChange}
        margin="normal"
        sx={{ width: "300px", height: "56px" }}
      />
      <FormControl fullWidth margin="normal">
        <InputLabel>Courses</InputLabel>
        <Select
          multiple
          name="courses"
          value={user.courses}
          onChange={handleChange}
          input={<Input />}
          renderValue={
            /*(selected) => selected.join(", ")*/ (selected) => (
              <Box sx={{ display: "flex", flexWrap: "wrap", gap: 0.5 }}>
                {selected.map((value) => (
                  <Typography key={value} sx={{ mr: 0.5 }}>
                    {value}
                  </Typography>
                ))}
              </Box>
            )
          }
          MenuProps={{
            PaperProps: {
              style: {
                maxHeight: 250, // Set a max height for the dropdown (the menu)
                overflow: "auto", // Enable scrolling when the dropdown exceeds max height
              },
            },
          }}
        >
          {courses.map((course) => (
            <MenuItem key={course} value={course}>
              <Checkbox checked={user.courses.indexOf(course) > -1} />
              <ListItemText primary={course} />
            </MenuItem>
          ))}
        </Select>
      </FormControl>
      <Button
        onClick={handleSubmit}
        variant="contained"
        /*fullWidth*/ sx={{ width: "300px", height: "56px" }}
      >
        Submit
      </Button>
    </Box>
  );
};

export default UserForm;
