import React, { useEffect, useState } from "react";
import { Container, Typography, Grid, Card, Box, Avatar } from "@mui/material";
import GroupIcon from "@mui/icons-material/Group";
import ExtensionIcon from "@mui/icons-material/Extension";
import UserForm from "../components/UserForm";
import UserList from "../components/UserList";
import SkillProgress from "../components/SkillProgress";

const Dashboard = () => {
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [challengeCount, setChallengeCount] = useState(0);

  const fetchUsers = async () => {
    try {
      const response = await fetch(
        `${process.env.REACT_APP_API_BASE_URL}/api/users`
      );
      const data = await response.json();
      setUsers(data);

      const uniqueCourses = new Set(data.flatMap((user) => user.courses));
      setChallengeCount(uniqueCourses.size);
    } catch (error) {
      console.error("Error fetching users:", error);
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  return (
    <Container>
      {/* Header */}
      <Box sx={{ display: "flex", alignItems: "center", my: 2 }}>
        <Avatar
          src="/logo192.png"
          alt="Logo"
          sx={{ width: 56, height: 56, mr: 2 }}
        />
        <Typography variant="h4">Community Skill Dashboard</Typography>
      </Box>

      {/* Summary Cards */}
      <Grid container spacing={3} sx={{ mb: 2 }}>
        <Grid item xs={12} md={4}>
          <Card sx={{ display: "flex", alignItems: "center", p: 2 }}>
            <GroupIcon sx={{ fontSize: 40, color: "#1976d2", mr: 2 }} />
            <Box>
              <Typography variant="h6">Total Students</Typography>
              <Typography variant="h5" fontWeight="bold">
                {users.length}
              </Typography>
            </Box>
          </Card>
        </Grid>

        <Grid item xs={12} md={4}>
          <Card sx={{ display: "flex", alignItems: "center", p: 2 }}>
            <ExtensionIcon sx={{ fontSize: 40, color: "#388e3c", mr: 2 }} />
            <Box>
              <Typography variant="h6">Total Challenges</Typography>
              <Typography variant="h5" fontWeight="bold">
                {challengeCount}
              </Typography>
            </Box>
          </Card>
        </Grid>

        {selectedUser && (
          <Grid item xs={12} md={4}>
            <Card sx={{ display: "flex", alignItems: "center", p: 2 }}>
              <ExtensionIcon sx={{ fontSize: 40, color: "#f57c00", mr: 2 }} />
              <Box>
                <Typography variant="h6">
                  {selectedUser.username}'s Challenges
                </Typography>
                <Typography variant="h5" fontWeight="bold">
                  {selectedUser.courses?.length || 0}
                </Typography>
              </Box>
            </Card>
          </Grid>
        )}
      </Grid>

      {/* User Form and List */}
      <Grid container spacing={3}>
        <Grid item xs={12} md={4}>
          <UserForm refresh={fetchUsers} />
        </Grid>
        <Grid item xs={12} md={8}>
          <UserList
            users={users}
            refresh={fetchUsers}
            onSelect={(user) => setSelectedUser(user)}
          />
          {selectedUser && <SkillProgress user={selectedUser} />}
        </Grid>
      </Grid>
    </Container>
  );
};

export default Dashboard;
