import React, { useEffect, useState } from "react";
import { Container, Grid, Typography } from "@mui/material";
import TotalStudentsCard from "../components/TotalStudentsCard.jsx";
import UserForm from "../components/UserForm.jsx";
import UserList from "../components/UserList.jsx";
import SkillProgress from "../components/SkillProgress.jsx";

const Dashboard = () => {
  const [students, setStudents] = useState([]);
  const [selectedStudent, setSelectedStudent] = useState(null);

  // Fetch students from the backend API
  const fetchStudents = async () => {
    try {
      const response = await fetch("http://localhost:5000/api/students");
      const data = await response.json();
      setStudents(data);
    } catch (error) {
      console.error("Error fetching students:", error);
    }
  };

  useEffect(() => {
    fetchStudents();
  }, []);

  return (
    <Container>
      <Typography variant="h4" gutterBottom>
        Student Dashboard
      </Typography>
      <Grid container spacing={3}>
        {/* Total Students Card */}
        <Grid item xs={12} md={4}>
          <TotalStudentsCard total={students.length} />
        </Grid>

        {/* User Form */}
        <Grid item xs={12} md={8}>
          <UserForm onStudentAdded={fetchStudents} />
        </Grid>

        {/* User List */}
        <Grid item xs={12}>
          <UserList students={students} onSelectStudent={setSelectedStudent} />
        </Grid>

        {/* Skill Progress */}
        {selectedStudent && (
          <Grid item xs={12}>
            <SkillProgress student={selectedStudent} />
          </Grid>
        )}
      </Grid>
    </Container>
  );
};

export default Dashboard;
