import React, { useState } from "react";
import {
  TextField,
  Button,
  MenuItem,
  Box,
  Typography,
  FormControl,
  InputLabel,
  Select,
  Checkbox,
  ListItemText,
  OutlinedInput,
} from "@mui/material";

const courses = [
  "JavaScript",
  "React",
  "Node.js",
  "CSS",
  "Python",
  "HTML",
  "C#",
  ".NET",
];

const UserForm = ({ refresh }) => {
  const [user, setUser] = useState({ username: "", courses: [] });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUser((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async () => {
    await fetch(`${process.env.REACT_APP_API_BASE_URL}/api/users`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(user),
    });
    setUser({ username: "", courses: [] });
    refresh();
  };

  const renderSelectedCourses = (selected) => (
    <Box
      sx={{
        display: "flex",
        flexWrap: "wrap",
        gap: 0.5,
        maxHeight: 120, // max visual height
        overflowY: "auto",
      }}
    >
      {selected.map((value) => (
        <Box
          key={value}
          sx={{
            backgroundColor: "#e0e0e0",
            px: 1,
            py: 0.5,
            borderRadius: 1,
            fontSize: 12,
          }}
        >
          {value}
        </Box>
      ))}
    </Box>
  );

  return (
    <Box>
      <Typography variant="h6" gutterBottom>
        Add User
      </Typography>

      {/* Username Field (unchanged) */}
      <TextField
        fullWidth
        label="Username"
        name="username"
        value={user.username}
        onChange={handleChange}
        margin="normal"
      />

      {/* Courses Select Field that grows vertically */}
      <FormControl fullWidth margin="normal">
        <InputLabel>Courses</InputLabel>
        <Select
          multiple
          name="courses"
          value={user.courses}
          onChange={handleChange}
          input={<OutlinedInput label="Courses" />}
          renderValue={renderSelectedCourses}
          sx={{
            "& .MuiSelect-select": {
              display: "flex",
              alignItems: "flex-start",
              flexWrap: "wrap",
              gap: "4px",
              minHeight: 56,
              overflowY: "auto",
            },
          }}
          MenuProps={{
            PaperProps: {
              style: {
                maxHeight: 300,
              },
            },
          }}
        >
          {courses.map((course) => (
            <MenuItem key={course} value={course}>
              <Checkbox checked={user.courses.includes(course)} />
              <ListItemText primary={course} />
            </MenuItem>
          ))}
        </Select>
      </FormControl>

      {/* Submit Button (unchanged) */}
      <Button
        onClick={handleSubmit}
        variant="contained"
        fullWidth
        sx={{ mt: 2 }}
      >
        Submit
      </Button>
    </Box>
  );
};

export default UserForm;
