import React, { useState } from "react";
import {
  TextField,
  Button,
  MenuItem,
  Box,
  Typography,
  FormControl,
  InputLabel,
  Select,
  Checkbox,
  ListItemText,
  OutlinedInput,
  Chip,
} from "@mui/material";

const courses = [
  "JavaScript",
  "React",
  "Node.js",
  "CSS",
  "Python",
  "HTML",
  "C#",
  ".NET",
];

const UserForm = ({ refresh, onUserAdded }) => {
  const [user, setUser] = useState({ username: "", courses: [] });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUser((prev) => ({
      ...prev,
      [name]: typeof value === "string" ? value.split(",") : value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const res = await fetch(`${process.env.REACT_APP_API_BASE_URL}/api/users`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(user),
    });

    const data = await res.json(); // Assuming backend returns the created user object
    if (res.ok && onUserAdded) {
      onUserAdded(data); // Notify parent to add the new user to list
    }

    setUser({ username: "", courses: [] });
    refresh(); // Optional: you can remove this if not needed
  };

  return (
    <Box>
      <Typography variant="h6" gutterBottom>
        Add User
      </Typography>

      <Box sx={{ width: "300px" }}>
        <TextField
          fullWidth
          label="Username"
          name="username"
          value={user.username}
          onChange={handleChange}
          margin="normal"
        />
      </Box>

      <FormControl fullWidth margin="normal">
        <InputLabel>Courses</InputLabel>
        <Select
          multiple
          name="courses"
          value={user.courses}
          onChange={handleChange}
          input={<OutlinedInput label="Courses" />}
          renderValue={(selected) => (
            <Box sx={{ display: "flex", flexWrap: "wrap", gap: 0.5 }}>
              {selected.map((value) => (
                <Chip key={value} label={value} color="primary" />
              ))}
            </Box>
          )}
        >
          {courses.map((course) => (
            <MenuItem key={course} value={course}>
              <Checkbox checked={user.courses.includes(course)} />
              <ListItemText primary={course} />
            </MenuItem>
          ))}
        </Select>
      </FormControl>

      <Box sx={{ width: "300px", mt: 2 }}>
        <Button onClick={handleSubmit} variant="contained" fullWidth>
          Submit
        </Button>
      </Box>
    </Box>
  );
};

export default UserForm;
