import React, { useEffect, useState } from "react";
import { Container, Typography, Grid, Paper, Box } from "@mui/material";
import UserForm from "../components/UserForm.jsx";
import UserList from "../components/UserList.jsx";
import SkillProgress from "../components/SkillProgress.jsx";
import TotalStudentsCard from "../components/TotalStudentsCard.jsx"; // Import the new card

const Dashboard = () => {
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [totalStudents, setTotalStudents] = useState(0); // State for total student count

  const fetchUsers = async () => {
    const res = await fetch("http://localhost:5000/api/users");
    const data = await res.json();
    setUsers(data);
  };

  const fetchTotalStudents = async () => {
    const res = await fetch("http://localhost:5000/api/users");
    const data = await res.json();
    setTotalStudents(data.length); // Set the total number of students
  };

  useEffect(() => {
    fetchUsers();
    fetchTotalStudents(); // Fetch the total students count when the component mounts
  }, []);

  return (
    <Container>
      <Typography variant="h4" my={2}>
        Community Skill Dashboard
      </Typography>
      <Grid container spacing={3}>
        <Grid item xs={12} md={4}>
          <UserForm refresh={fetchUsers} />
        </Grid>
        <Grid item xs={12} md={8}>
          {/* Add the new Total Students Card */}
          <Box mb={3}>
            <TotalStudentsCard total={totalStudents} />
          </Box>
          <UserList
            users={users}
            refresh={fetchUsers}
            onSelect={(user) => setSelectedUser(user)}
          />
          {selectedUser && <SkillProgress user={selectedUser} />}
        </Grid>
      </Grid>
    </Container>
  );
};

export default Dashboard;
